import React, { useState, useEffect } from "react";
import { io } from "socket.io-client";

const socket = io("http://localhost:3001"); // adjust if backend runs elsewhere

export default function YahtzeeGame() {
  const [gameId, setGameId] = useState("");
  const [name, setName] = useState("");
  const [joined, setJoined] = useState(false);
  const [gameState, setGameState] = useState(null);
  const [dice, setDice] = useState([]);

  // listen for updates from server
  useEffect(() => {
    socket.on("game-updated", (state) => {
      setGameState(state);
      setDice(state.dice);
    });

    socket.on("diceRolled", ({ player, dice }) => {
      console.log("Dice rolled by", player, dice);
      setDice(dice);
    });

    return () => {
      socket.off("game-updated");
      socket.off("diceRolled");
    };
  }, []);

  const handleCreate = () => {
    socket.emit("create-game", { name }, (res) => {
      if (res.ok) {
        setGameId(res.gameId);
        setGameState(res.state);
        setJoined(true);
      }
    });
  };

  const handleJoin = () => {
    socket.emit("join-game", { gameId, name }, (res) => {
      if (res.ok) {
        setGameState(res.state);
        setJoined(true);
      } else {
        alert(res.error);
      }
    });
  };

  const handleRoll = () => {
    socket.emit("roll-dice", { gameId }, (res) => {
      if (!res.ok) {
        alert(res.error || "Roll failed");
      }
    });
  };

  // ðŸ”¥ FIX: moved here, not inside JSX
  const handleScore = (category) => {
    console.log("Scoring", category, dice);
    socket.emit("choose-score", { gameId, category }, (res) => {
      if (!res.ok) {
        alert(res.error || "Score failed");
      }
    });
  };

  return (
    <div style={{ padding: "2rem" }}>
      {!joined ? (
        <div>
          <input
            placeholder="Your name"
            value={name}
            onChange={(e) => setName(e.target.value)}
          />
          <button onClick={handleCreate}>Create Game</button>
          <br />
          <input
            placeholder="Game ID"
            value={gameId}
            onChange={(e) => setGameId(e.target.value)}
          />
          <button onClick={handleJoin}>Join Game</button>
        </div>
      ) : (
        <div>
          <h2>Game: {gameId}</h2>
          {gameState && (
            <>
              <p>Status: {gameState.status}</p>

              <p>
                Players:{" "}
                {gameState.players.map((p, idx) => (
                  <span
                    key={p.socketId}
                    style={{
                      fontWeight:
                        idx === gameState.currentPlayerIndex ? "bold" : "normal",
                      marginRight: "1rem",
                    }}
                  >
                    {p.name} ({p.total})
                  </span>
                ))}
              </p>

              <div style={{ fontSize: "2rem" }}>
                {dice.map((d, i) => (
                  <span key={i} style={{ margin: "0 10px" }}>
                    ðŸŽ² {d}
                  </span>
                ))}
              </div>

              <button onClick={handleRoll}>Roll Dice</button>

             <h3>Scoreboard</h3>
<table border="1" cellPadding="5">
  <thead>
    <tr>
      <th>Player</th>
      <th>Ones</th>
      <th>Twos</th>
      <th>Threes</th>
      <th>Fours</th>
      <th>Fives</th>
      <th>Sixes</th>
      <th>3 Kind</th>
      <th>4 Kind</th>
      <th>Full House</th>
      <th>Short Straight</th>
      <th>Long Straight</th>
      <th>Yahtzee</th>
      <th>Chance</th>
      <th>Total</th>
    </tr>
  </thead>
  <tbody>
    {gameState.players.map((p) => (
      <tr key={p.socketId}>
        <td>{p.name}</td>
        <td>{p.scores.aces ?? "-"}</td>
        <td>{p.scores.twos ?? "-"}</td>
        <td>{p.scores.threes ?? "-"}</td>
        <td>{p.scores.fours ?? "-"}</td>
        <td>{p.scores.fives ?? "-"}</td>
        <td>{p.scores.sixes ?? "-"}</td>
        <td>{p.scores.threeKind ?? "-"}</td>
        <td>{p.scores.fourKind ?? "-"}</td>
        <td>{p.scores.fullHouse ?? "-"}</td>
        <td>{p.scores.shortStraight ?? "-"}</td>
        <td>{p.scores.longStraight ?? "-"}</td>
        <td>{p.scores.yahtzee ?? "-"}</td>
        <td>{p.scores.chance ?? "-"}</td>
        <td>{p.total}</td>
      </tr>
    ))}
  </tbody>
</table>

              <h3>Choose a Category</h3>
<div style={{ display: "flex", flexWrap: "wrap", gap: "0.5rem" }}>
  {[
    "aces", "twos", "threes", "fours", "fives", "sixes",
    "threeKind", "fourKind", "fullHouse",
    "shortStraight", "longStraight",
    "yahtzee", "chance"
  ].map((cat) => (
    <button
      key={cat}
      onClick={() => handleScore(cat)}
      disabled={
        gameState.players.find((p) => p.socketId === socket.id)?.scores[cat] !== undefined ||
        gameState.players[gameState.currentPlayerIndex].socketId !== socket.id
      }
    >
      {cat}
    </button>
  ))}
</div>

            </>
          )}
        </div>
      )}
    </div>
  );
}
